<?php    /**     * Created by PhpStorm.     * User: Administrator     * Date: 2019/1/21 0021     * Time: 上午 11:16     */        namespace app\index\controller;    use think\Controller;    use think\Db;    use think\Request;    class Common extends  Controller    {        public  function  _initialize()        {            $this->assign('request_action', Request::instance()->action());            //menu            $menu = Db::name('menu')->where('status' , 1)->order('sort asc')->select();            if (!empty($menu)) $this->assign('menu', $menu);            //设置            $contact =   Db::name('contact')-> find();            $setting =   Db::name('setting')-> find();            if (!empty( $setting['title'])) $setting['title'] = unserialize( $setting['title']);            if (!empty( $setting['keywords']))$setting['keywords'] = unserialize( $setting['keywords']);            if (!empty( $setting['description'])) $setting['intro'] = unserialize( $setting['description']);            if (!empty( $contact['wechat_code'])) $setting['wechat_code'] = $contact['wechat_code'];            if (!empty( $contact['contacts_tel'])) $setting['contacts_tel'] = $contact['contacts_tel'];            $this->assign('setting', $setting);        }            }